import { useState } from 'react';
import * as C from './App.styles';
import { Item } from './types/Item';
import {ListItem} from './components/ListItem/index'

const App = () => {
  const [list, setList] = useState<Item[]>([
    {id: 1, name: 'Lavar os sapatos', done: false},
    {id: 2, name: 'Comprar açucar', done: false}
  ])


  return(
    <C.Container>
      <C.Area>
        <C.Header>Lista de Tarefas</C.Header>

        {/*Area de adicionar nova tareta */}

        {
          list.map((item, index)=> (
            <div>{item.name}</div>
          ))
        }


      </C.Area>
    </C.Container>
  );
}

export default App;
