import styled from 'styled-components';

export const Container = styled.div`
    background-color: #17181F;
    color:#797A81;
    min-height:100vh;
`;

export const Area = styled.div`
    margin:auto;
    max-width:980px;
    padding:10px;
`;

export const Header = styled.h1`
    margin: 0;
    padding: 0;
    color: #FFF;
    border-bottom: solid 1px #444;
    text-align: center;
`;
